mapper.select:
select bno, title, writer, view_cnt, comment_cnt, reg_date
from board
where bno = #{bno}

mapper.update:
UPDATE board
SET   title = #{title}
  , content = #{content}
  , up_date = now()
WHERE bno = #{bno}

UPDATE board
SET   view_cnt = view_cnt + 1
WHERE bno = #{bno}

UPDATE board
SET   comment_cnt = comment_cnt + #{cnt}
WHERE bno = #{bno}
